import java.util.Date;

public class Sample_dataType01 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("\uD64D"); //'홍' 유니코드
		System.out.println("홍"); //"" 문자열
		System.out.println(100); //정수 
		System.out.println(3.14); //실수, double과 동일(기본 값)
		System.out.println(3.14F); // 실수, float
		System.out.println(3.14D); // 실수, double
		System.out.println('\uD64D'); //하나의 문자, '홍' 유니코드
		System.out.println(true); 
		System.out.println(false);
	
	}

}
